import TodoList from "./TodoList";
import { useState, useRef } from 'react'
import uuidv4 from 'uuid/v4'

function App() {
  // At the start there is an empty list of todos so we pass an empty array 
  // This returns an array which can be destrructured 
  const [todos, setTodos] = useState([])
  const todoNameRef = useRef()

  function handleOnClick(e) {
    let name = todoNameRef.current.value

    if (name === '') {
      return
    } else {
      setTodos(prevTodos => {
        return [...prevTodos, { id: uuidv4(), name: name, complete: false }]
      })
    }

    todoNameRef.current.value = null
  }

  return (
    <>
      {/* Sending the totos prop to the Todos function*/}
      <TodoList todoList={todos} />

      <input type='text' ref={todoNameRef}></input>
      <button onClick={handleOnClick}>Add Todo</button>
      <button>Clear Todo</button>
      <div>0 Left</div>
    </>
  );
}

export default App;
