import TodoList from "./TodoList";
import { useState, useRef, useEffect } from 'react'
import { v4 as uuidv4 } from 'uuid';

function App() {
  // This returns an array which can be destrructured; the first is the actual array and the second is the function which modifies the array 
  // Use Ref is used to persist values between renders; e.g. the input box 
  const LOCAL_STORAGE_KEY = 'todoApp.todos'
  const [todos, setTodos] = useState([])
  const todoNameRef = useRef()

  // This retirves the set variables and only runs in the beginning since [] cant change afterwards 
  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))

    if (storedTodos) setTodos(storedTodos)
  }, [])

  // This stores the todolist in local storage whenever the todos change; even if the page is refreshed the todos are stored 
  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(todos))
  }, [todos])

  // This function will be called whenever the button is clicked 
  function handleOnClick(e) {
    let name = todoNameRef.current.value

    if (name === '') {
      return
    } else {
      setTodos(prevTodos => {
        return [...prevTodos, { id: uuidv4(), name: name, complete: false }]
      })
    }

    todoNameRef.current.value = null
  }

  function toggleTodo(id) {
    const newTodos = [...todos]

    const todo = newTodos.find(todo => todo.id === id)
    todo.complete = !todo.complete
    setTodos(newTodos)
  }

  function clearTodos() {
    const newTodos = [...todos]
    newTodos = todos.filter(todo => !todo.complete)
    setTodos(newTodos)
  }

  return (
    <>
      {/* Sending the totos prop to the Todos function; this then renders the list on the page */}
      <TodoList todoList={todos} toggleTodo={toggleTodo} />

      {/* The input from the text type will be held in todoNameRef */}
      <input type='text' ref={todoNameRef}></input>
      <button onClick={handleOnClick}>Add Todo</button>
      <button onClick={clearTodos}>Clear Completed</button>
      <div>{todos.filter(todo => !todo.complete).length} Left</div>
    </>
  );
}

export default App;
